import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  Download, 
  RefreshCw, 
  Wifi, 
  WifiOff, 
  Bell, 
  BellOff,
  Smartphone,
  Monitor,
  Share2,
  Settings,
  Info,
  CheckCircle,
  AlertTriangle,
  Clock,
  Trash2,
  Zap,
  Shield,
  Activity
} from 'lucide-react';
import usePWA from '@/hooks/usePWA';

const PWAStatus: React.FC = () => {
  const {
    isInstallable,
    isInstalled,
    isOffline,
    isUpdateAvailable,
    offlineActions,
    capabilities,
    installPWA,
    updatePWA,
    requestNotificationPermission,
    subscribeToPushNotifications,
    clearOfflineActions,
    shareContent,
    getAppInfo
  } = usePWA();

  const [notificationPermission, setNotificationPermission] = useState<NotificationPermission>('default');
  const [isInstalling, setIsInstalling] = useState(false);
  const [isUpdating, setIsUpdating] = useState(false);
  const [appInfo, setAppInfo] = useState<any>(null);
  const [showPWAPrompt, setShowPWAPrompt] = useState(false);

  useEffect(() => {
    // Check notification permission
    if ('Notification' in window) {
      setNotificationPermission(Notification.permission);
    }

    // Load app info
    getAppInfo().then(setAppInfo);

    // Show PWA prompt for non-installed users
    if (isInstallable && !isInstalled && !localStorage.getItem('pwa-prompt-dismissed')) {
      setTimeout(() => setShowPWAPrompt(true), 5000);
    }
  }, [isInstallable, isInstalled, getAppInfo]);

  const handleInstall = async () => {
    setIsInstalling(true);
    try {
      const success = await installPWA();
      if (success) {
        setShowPWAPrompt(false);
      }
    } catch (error) {
      console.error('Installation failed:', error);
    } finally {
      setIsInstalling(false);
    }
  };

  const handleUpdate = async () => {
    setIsUpdating(true);
    try {
      await updatePWA();
    } catch (error) {
      console.error('Update failed:', error);
    } finally {
      setIsUpdating(false);
    }
  };

  const handleNotificationPermission = async () => {
    const permission = await requestNotificationPermission();
    setNotificationPermission(permission);

    if (permission === 'granted') {
      await subscribeToPushNotifications();
    }
  };

  const handleShare = async () => {
    const shareData = {
      title: 'SecureFlow Automaton',
      text: 'Check out this amazing DevSecOps automation platform!',
      url: window.location.origin
    };

    const success = await shareContent(shareData);
    if (!success) {
      // Fallback to copying URL
      navigator.clipboard?.writeText(window.location.origin);
    }
  };

  const dismissPWAPrompt = () => {
    setShowPWAPrompt(false);
    localStorage.setItem('pwa-prompt-dismissed', 'true');
  };

  const formatBytes = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getConnectionStatus = () => {
    if (!('connection' in navigator)) return 'Unknown';
    const connection = (navigator as any).connection;
    return `${connection.effectiveType?.toUpperCase() || 'Unknown'} (${connection.downlink || '?'} Mbps)`;
