                  ) : isInstallable ? (
                    <Button onClick={handleInstall} disabled={isInstalling}>
                      {isInstalling ? (
                        <>
                          <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                          Installing...
                        </>
                      ) : (
                        <>
                          <Download className="w-4 h-4 mr-2" />
