        const redirectUrl = `${window.location.origin}/`;
        const { error } = await supabase.auth.signUp({
          email: form.email,
          password: form.password,
          options: { 
            emailRedirectTo: redirectUrl,
            data: {
              registration_timestamp: new Date().toISOString(),
              password_strength: passwordValidation?.strength
            }
          },
        });

        if (error) {
          setErr(error.message);
          SecurityLogger.logEvent('signup_failed', { 
            email: form.email, 
            error: error.message 
          });
        } else {
          setErr("Signup successful! Please check your email to confirm your account.");
          SecurityLogger.logEvent('signup_successful', { 
            email: form.email 
          });
        }
      } else {
        const { error } = await supabase.auth.signInWithPassword({ 
          email: form.email, 
          password: form.password 
        });

        if (error) {
          setErr(error.message);
          SecurityLogger.logEvent('login_failed', { 
            email: form.email, 
            error: error.message 
          });
        } else {
          SecurityLogger.logEvent('login_successful', { 
            email: form.email 
          });
        }
      }
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'An unexpected error occurred';
      setErr(errorMessage);
      SecurityLogger.logEvent('auth_error', { 
        email: form.email, 
        error: errorMessage 
      });
    } finally {
      setLoading(false);
    }
  };

  const getPasswordStrengthColor = (strength: string) => {
    switch (strength) {
      case 'very-strong': return 'text-green-600';
      case 'strong': return 'text-green-500';
      case 'medium': return 'text-yellow-500';
      default: return 'text-red-500';
    }
  };

  const getPasswordStrengthWidth = (strength: string) => {
    switch (strength) {
      case 'very-strong': return 'w-full';
      case 'strong': return 'w-3/4';
      case 'medium': return 'w-1/2';
      default: return 'w-1/4';
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <Shield className="h-12 w-12 text-blue-600" />
          </div>
          <CardTitle className="text-2xl font-bold">
            {isSignup ? "Create Account" : "Sign In"}
          </CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <Input
                placeholder="Email address"
                name="email"
                type="email"
                required
                value={form.email}
                autoComplete="username"
                onChange={handleChange}
                className="w-full"
              />
            </div>

            <div className="relative">
              <Input
                placeholder="Password"
                name="password"
                type={showPassword ? "text" : "password"}
                required
                autoComplete={isSignup ? "new-password" : "current-password"}
                value={form.password}
                onChange={handleChange}
                className="w-full pr-10"
              />
              <button
                type="button"
                onClick={() => setShowPassword(!showPassword)}
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-500 hover:text-gray-700"
              >
                {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
              </button>
            </div>

            {isSignup && (
              <>
                <div>
                  <Input
                    placeholder="Confirm password"
                    name="confirmPassword"
                    type={showPassword ? "text" : "password"}
                    required
                    autoComplete="new-password"
                    value={form.confirmPassword}
                    onChange={handleChange}
                    className="w-full"
                  />
                </div>

                {/* Password strength indicator */}
                {passwordValidation && form.password && (
                  <div className="space-y-2">
                    <div className="flex items-center justify-between text-sm">
                      <span>Password strength:</span>
                      <span className={`font-medium capitalize ${getPasswordStrengthColor(passwordValidation.strength)}`}>
                        {passwordValidation.strength.replace('-', ' ')}
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div 
                        className={`h-2 rounded-full transition-all duration-300 ${
                          passwordValidation.strength === 'very-strong' ? 'bg-green-600' :
                          passwordValidation.strength === 'strong' ? 'bg-green-500' :
                          passwordValidation.strength === 'medium' ? 'bg-yellow-500' :
                          'bg-red-500'
                        } ${getPasswordStrengthWidth(passwordValidation.strength)}`}
                      />
                    </div>

                    {/* Password requirement errors */}
                    {passwordValidation.errors.length > 0 && (
                      <div className="space-y-1">
                        {passwordValidation.errors.map((error, index) => (
                          <div key={index} className="flex items-center text-xs text-red-600">
                            <AlertTriangle className="h-3 w-3 mr-1" />
                            {error}
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </>
            )}

            <Button 
              className="w-full" 
              type="submit" 
              disabled={loading || (isSignup && passwordValidation && !passwordValidation.isValid)}
            >
              {loading ? "Please wait..." : isSignup ? "Create Account" : "Sign In"}
            </Button>

            {err && (
              <Alert variant={err.includes("successful") ? "default" : "destructive"}>
                <AlertDescription className="flex items-center">
                  {err.includes("successful") ? (
                    <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                  ) : (
                    <AlertTriangle className="h-4 w-4 mr-2" />
                  )}
                  {err}
                </AlertDescription>
              </Alert>
            )}

            <div className="text-center text-sm">
              {isSignup ? (
                <>
                  Already have an account?{" "}
                  <button
                    type="button"
                    className="text-blue-600 hover:text-blue-800 font-medium"
                    onClick={() => {
                      setIsSignup(false);
                      setPasswordValidation(null);
                      setForm({ email: "", password: "", confirmPassword: "" });
                      setErr(null);
                    }}
                  >
                    Sign in
                  </button>
                </>
              ) : (
                <>
                  Don't have an account?{" "}
                  <button
                    type="button"
                    className="text-blue-600 hover:text-blue-800 font-medium"
                    onClick={() => {
                      setIsSignup(true);
                      setPasswordValidation(null);
                      setForm({ email: "", password: "", confirmPassword: "" });
                      setErr(null);
                    }}
                  >
                    Create account
                  </button>
                </>
              )}
            </div>
          </form>

          {isSignup && (
            <div className="mt-6 p-4 bg-blue-50 rounded-lg">
              <h4 className="text-sm font-medium text-blue-900 mb-2">Password Requirements:</h4>
              <ul className="text-xs text-blue-800 space-y-1">
                <li>• At least 12 characters long</li>
                <li>• Contains uppercase and lowercase letters</li>
                <li>• Includes numbers and special characters</li>
                <li>• No common patterns or dictionary words</li>
              </ul>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default Auth;

import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import { useAuth } from "@/components/AuthContext";
import { Link } from "react-router-dom";

const NOT_FOUND_FN_URL =
  "https://znubqwefuxqkzjgtrdcf.functions.supabase.co/not-found-log";

const NotFound = () => {
  const location = useLocation();
  const [backendMessage, setBackendMessage] = useState<string | null>(null);
  const [tip, setTip] = useState<string>("");
  const { user } = useAuth();

  useEffect(() => {
    // POST log to backend
    fetch(NOT_FOUND_FN_URL, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        path: location.pathname,
        referrer: window?.document?.referrer ?? "",
        userAgent: navigator.userAgent,
        userId: user?.id,
      }),
    }).catch((e) => {});

    // Fetch custom error (GET)
    fetch(`${NOT_FOUND_FN_URL}?path=${encodeURIComponent(location.pathname)}`)
      .then(res => res.json())
      .then(data => {
        setBackendMessage(data?.message || null);
        setTip(data?.tip || "");
      })
      .catch(() => {});
  }, [location.pathname, user]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="text-center">
        <h1 className="text-4xl font-bold mb-4">404</h1>
        <p className="text-xl text-gray-600 mb-4">
          {backendMessage || "Oops! Page not found."}
        </p>
        {tip && <p className="text-sm text-muted-foreground mb-3">{tip}</p>}
        <Link to="/" className="text-blue-500 hover:text-blue-700 underline">
          Return to Home
        </Link>
      </div>
    </div>
  );
};

