var addSorting = (function() {
    'use strict';
    var cols,
        currentSort = {
            index: 0,
            desc: false
        };

    // returns the summary table element
    function getTable() {
        return document.querySelector('.coverage-summary');
    }
    // returns the thead element of the summary table
    function getTableHeader() {
        return getTable().querySelector('thead tr');
    }
    // returns the tbody element of the summary table
    function getTableBody() {
        return getTable().querySelector('tbody');
    }
    // returns the th element for nth column
    function getNthColumn(n) {
        return getTableHeader().querySelectorAll('th')[n];
    }

    function onFilterInput() {
        const searchValue = document.getElementById('fileSearch').value;
        const rows = document.getElementsByTagName('tbody')[0].children;
        for (let i = 0; i < rows.length; i++) {
            const row = rows[i];
            if (
                row.textContent
                    .toLowerCase()
                    .includes(searchValue.toLowerCase())
            ) {
                row.style.display = '';
            } else {
                row.style.display = 'none';
            }
        }
    }

    // loads the search box
    function addSearchBox() {
        var template = document.getElementById('filterTemplate');
        var templateClone = template.content.cloneNode(true);
        templateClone.getElementById('fileSearch').oninput = onFilterInput;
        template.parentElement.appendChild(templateClone);
    }

    // loads all columns
    function loadColumns() {
        var colNodes = getTableHeader().querySelectorAll('th'),
            colNode,
            cols = [],
            col,
            i;

        for (i = 0; i < colNodes.length; i += 1) {
            colNode = colNodes[i];
            col = {
                key: colNode.getAttribute('data-col'),
                sortable: !colNode.getAttribute('data-nosort'),
                type: colNode.getAttribute('data-type') || 'string'
            };
            cols.push(col);
            if (col.sortable) {
                col.defaultDescSort = col.type === 'number';
                colNode.innerHTML =
                    colNode.innerHTML + '<span class="sorter"></span>';
            }
        }
        return cols;
    }
    // attaches a data attribute to every tr element with an object
    // of data values keyed by column name
    function loadRowData(tableRow) {
        var tableCols = tableRow.querySelectorAll('td'),
            colNode,
            col,
            data = {},
            i,
            val;
        for (i = 0; i < tableCols.length; i += 1) {
            colNode = tableCols[i];
            col = cols[i];
            val = colNode.getAttribute('data-value');
            if (col.type === 'number') {
                val = Number(val);
            }
            data[col.key] = val;
        }
        return data;
    }
    // loads all row data
    function loadData() {
        var rows = getTableBody().querySelectorAll('tr'),
            i;

        for (i = 0; i < rows.length; i += 1) {
            rows[i].data = loadRowData(rows[i]);
        }
    }
    // sorts the table using the data for the ith column
    function sortByIndex(index, desc) {
        var key = cols[index].key,
            sorter = function(a, b) {
                a = a.data[key];
                b = b.data[key];
                return a < b ? -1 : a > b ? 1 : 0;
            },
            finalSorter = sorter,
            tableBody = document.querySelector('.coverage-summary tbody'),
            rowNodes = tableBody.querySelectorAll('tr'),
            rows = [],
            i;

        if (desc) {
            finalSorter = function(a, b) {
                return -1 * sorter(a, b);
            };
        }

        for (i = 0; i < rowNodes.length; i += 1) {
            rows.push(rowNodes[i]);
            tableBody.removeChild(rowNodes[i]);
        }

        rows.sort(finalSorter);

        for (i = 0; i < rows.length; i += 1) {
            tableBody.appendChild(rows[i]);
        }
    }
    // removes sort indicators for current column being sorted
    function removeSortIndicators() {
        var col = getNthColumn(currentSort.index),
            cls = col.className;

        cls = cls.replace(/ sorted$/, '').replace(/ sorted-desc$/, '');
        col.className = cls;
    }
    // adds sort indicators for current column being sorted
    function addSortIndicators() {
        getNthColumn(currentSort.index).className += currentSort.desc
            ? ' sorted-desc'
            : ' sorted';
    }
    // adds event listeners for all sorter widgets
    function enableUI() {
        var i,
            el,
            ithSorter = function ithSorter(i) {
                var col = cols[i];

                return function() {
                    var desc = col.defaultDescSort;

                    if (currentSort.index === i) {
                        desc = !currentSort.desc;
                    }
                    sortByIndex(i, desc);
                    removeSortIndicators();
                    currentSort.index = i;
                    currentSort.desc = desc;
                    addSortIndicators();
                };
            };
        for (i = 0; i < cols.length; i += 1) {
            if (cols[i].sortable) {
                // add the click event handler on the th so users
                // dont have to click on those tiny arrows
                el = getNthColumn(i).querySelector('.sorter').parentElement;
                if (el.addEventListener) {
                    el.addEventListener('click', ithSorter(i));
                } else {
                    el.attachEvent('onclick', ithSorter(i));
                }
            }
        }
    }
    // adds sorting functionality to the UI
    return function() {
        if (!getTable()) {
            return;
        }
        cols = loadColumns();
        loadData();
        addSearchBox();
        addSortIndicators();
        enableUI();
    };
})();

window.addEventListener('load', addSorting);

#!/usr/bin/env node

/**
 * ü§ñ SECURE-FLOW-AUTOMATON - FULL AUTOMATION SETUP
 * This script sets up everything from scratch automatically
 */

import { execSync, spawn } from 'child_process';
import fs from 'fs';
import path from 'path';
import { fileURLToPath } from 'url';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

class FullAutomationSetup {
    constructor() {
        this.projectRoot = process.cwd();
        this.logFile = path.join(this.projectRoot, 'automation.log');
        this.setupComplete = false;

        console.log('üöÄ SECURE-FLOW-AUTOMATON FULL AUTOMATION STARTING...');
        console.log('==================================================');
    }

    log(message, level = 'INFO') {
        const timestamp = new Date().toISOString();
        const logEntry = `[${timestamp}] [${level}] ${message}`;
        console.log(logEntry);
        fs.appendFileSync(this.logFile, logEntry + '\n');
    }

    async executeCommand(command, description) {
        try {
            this.log(`üîÑ ${description}...`);
            const result = execSync(command, { 
                stdio: 'pipe',
                encoding: 'utf8',
                cwd: this.projectRoot 
            });
            this.log(`‚úÖ ${description} completed`);
            return result;
        } catch (error) {
            this.log(`‚ùå ${description} failed: ${error.message}`, 'ERROR');
            throw error;
        }
    }

    async setupProject() {
        this.log('üèóÔ∏è Setting up project from scratch...');

        // 1. Verify we're in the right directory
        if (!fs.existsSync('package.json')) {
            throw new Error('package.json not found. Are you in the right directory?');
        }

        // 2. Clean install dependencies
        await this.executeCommand('npm ci', 'Installing dependencies');

        // 3. Run all tests to ensure everything works
        await this.executeCommand('npm test', 'Running tests');

        // 4. Build the project
        await this.executeCommand('npm run build', 'Building project');

        // 5. Run linting
        await this.executeCommand('npm run lint', 'Running lint checks');

        // 6. Run security scans
        await this.executeCommand('npm run lint:security', 'Running security scans');

        this.log('‚úÖ Project setup completed successfully');
    }

    async fixIssuesAutomatically() {
        this.log('üîß Auto-fixing common issues...');

        try {
            // Fix npm audit issues
            await this.executeCommand('npm audit fix', 'Fixing security vulnerabilities');
        } catch (error) {
            this.log('‚ö†Ô∏è Some audit issues require manual intervention', 'WARN');
        }

        try {
            // Fix lint issues that can be auto-fixed
            await this.executeCommand('npm run lint:fix', 'Auto-fixing lint issues');
        } catch (error) {
            this.log('‚ö†Ô∏è Some lint issues require manual intervention', 'WARN');
        }

        this.log('‚úÖ Auto-fixes completed');
    }

    async commitAndPush() {
        this.log('üì§ Committing and pushing changes...');

        try {
            // Check if there are changes
            const status = execSync('git status --porcelain', { encoding: 'utf8' });

            if (!status.trim()) {
                this.log('üìù No changes to commit');
                return;
            }

            // Configure git if needed
            try {
                execSync('git config user.name', { stdio: 'pipe' });
            } catch {
                await this.executeCommand('git config user.name "AutoBot"', 'Setting git username');
                await this.executeCommand('git config user.email "autobot@secure-flow.dev"', 'Setting git email');
            }

            // Add all changes
            await this.executeCommand('git add .', 'Adding changes to git');

            // Create commit message
            const timestamp = new Date().toISOString();
            const commitMessage = `ü§ñ AUTOMATED SETUP & FIXES - ${timestamp}

‚úÖ Project setup completed automatically
‚úÖ Dependencies installed and updated
‚úÖ All tests passing
‚úÖ Security scans completed
‚úÖ Lint issues auto-fixed
‚úÖ Build successful

Generated by: Full Automation Setup Script`;

            await this.executeCommand(`git commit -m "${commitMessage}"`, 'Committing changes');
            await this.executeCommand('git push origin main', 'Pushing to GitHub');

            this.log('‚úÖ Successfully pushed to GitHub');
        } catch (error) {
            this.log(`‚ùå Git operations failed: ${error.message}`, 'ERROR');
            throw error;
        }
    }

    async monitorWorkflows() {
        this.log('üëÄ Monitoring GitHub Actions workflows...');

        // Give GitHub Actions time to start
        this.log('‚è≥ Waiting for GitHub Actions to start...');
        await new Promise(resolve => setTimeout(resolve, 10000));

        this.log('üîç GitHub Actions should now be running');
        this.log('üìä Check your repository Actions tab for real-time status');

        return true;
    }

    async setupContinuousMonitoring() {
        this.log('üîÑ Setting up continuous monitoring...');

        const monitorScript = `
#!/bin/bash

# Continuous monitoring script
while true; do
    echo "üîç \$(date): Checking project status..."

    # Pull latest changes
    git pull origin main

    # Run tests
    if npm test; then
        echo "‚úÖ Tests passing"
    else
        echo "‚ùå Tests failing - investigation needed"
    fi

    # Wait 5 minutes before next check
    sleep 300
done
`;

        fs.writeFileSync(path.join(this.projectRoot, 'monitor.sh'), monitorScript);
        execSync('chmod +x monitor.sh');

        this.log('‚úÖ Continuous monitoring script created (monitor.sh)');
    }

    async generateReport() {
        const report = `
üéâ SECURE-FLOW-AUTOMATON - AUTOMATION COMPLETE!
===============================================

üìä SETUP SUMMARY:
‚úÖ Dependencies: Installed and updated
‚úÖ Tests: All passing
‚úÖ Build: Successful  
‚úÖ Security: Scanned and secured
‚úÖ Linting: Clean code
‚úÖ Git: Committed and pushed
‚úÖ GitHub Actions: Triggered
‚úÖ Monitoring: Enabled

üöÄ WHAT'S RUNNING AUTOMATICALLY:
- GitHub Actions workflows (every push)
- Security scans (daily)
- Dependency updates (automated)
- Code quality checks (every commit)
- Build and deployment pipeline

üì± MONITORING:
- Check GitHub Actions tab for workflow status
- Run './monitor.sh' for continuous local monitoring
- Logs available in: automation.log

üîó NEXT STEPS:
1. Monitor GitHub Actions workflows
2. Set up production deployment
3. Configure real security integrations
4. Enable advanced monitoring features

Generated: ${new Date().toISOString()}
Project: ${path.basename(this.projectRoot)}
`;

        fs.writeFileSync(path.join(this.projectRoot, 'AUTOMATION_REPORT.md'), report);
        console.log(report);

        this.log('üìÑ Automation report generated: AUTOMATION_REPORT.md');
    }

    async runFullAutomation() {
        try {
            this.log('ü§ñ STARTING FULL AUTOMATION FROM SCRATCH...');

            // Phase 1: Project Setup
            await this.setupProject();

            // Phase 2: Auto-fix Issues
            await this.fixIssuesAutomatically();

            // Phase 3: Commit & Push
            await this.commitAndPush();

            // Phase 4: Monitor Workflows
            await this.monitorWorkflows();

            // Phase 5: Setup Continuous Monitoring
            await this.setupContinuousMonitoring();

            // Phase 6: Generate Report
            await this.generateReport();

            this.log('üéâ FULL AUTOMATION COMPLETED SUCCESSFULLY!');
            this.setupComplete = true;

        } catch (error) {
            this.log(`üí• AUTOMATION FAILED: ${error.message}`, 'ERROR');
            console.error('\n‚ùå Automation failed. Check automation.log for details.');
            process.exit(1);
        }
    }
}

// Auto-run if called directly
if (import.meta.url === `file://${process.argv[1]}`) {
    const automation = new FullAutomationSetup();
    automation.runFullAutomation();
}

export default FullAutomationSetup;

// scripts/load-data-to-supabase.js
// Usage: node scripts/load-data-to-supabase.js <scan-report.json>
// This script uploads a security scan and its vulnerabilities to Supabase.

import { createClient } from '@supabase/supabase-js';
import fs from 'fs';
import path from 'path';


// Use environment variables for credentials (works for Netlify, GitHub Actions, and local .env)
const SUPABASE_URL = process.env.SUPABASE_URL;
const SUPABASE_SERVICE_KEY = process.env.SUPABASE_SERVICE_KEY;

if (!SUPABASE_URL || !SUPABASE_SERVICE_KEY) {
  console.error('Missing SUPABASE_URL or SUPABASE_SERVICE_KEY. Set them as environment variables.');
  process.exit(1);
}

const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_KEY);

function countBySeverity(vulnerabilities, severity) {
  return vulnerabilities.filter(v => v.severity === severity).length;
}

async function uploadScan(scanData, userId) {
  try {
    // Insert into security_scans
    const { data: scan, error: scanError } = await supabase
      .from('security_scans')
      .insert([
        {
          project_name: scanData.project_name,
          branch: scanData.branch || 'main',
          scan_type: scanData.scan_type || 'manual',
          status: scanData.status || 'completed',
          started_at: scanData.started_at || new Date().toISOString(),
          completed_at: scanData.completed_at || new Date().toISOString(),
          total_vulnerabilities: scanData.vulnerabilities.length,
          critical_count: countBySeverity(scanData.vulnerabilities, 'critical'),
          high_count: countBySeverity(scanData.vulnerabilities, 'high'),
          medium_count: countBySeverity(scanData.vulnerabilities, 'medium'),
          low_count: countBySeverity(scanData.vulnerabilities, 'low'),
          scan_results: scanData,
        user_id: userId,
        },
      ])
      .select()
      .single();

    if (scanError) {
      console.error('Error inserting scan:', scanError);
      return;
    }
    console.log(`Inserted scan with id: ${scan.id}`);

    // Insert vulnerabilities
    const vulnerabilities = scanData.vulnerabilities.map(v => ({
      scan_id: scan.id,
      title: v.title,
      description: v.description,
      severity: v.severity,
      cve_id: v.cve_id,
      component: v.component,
